import{_ as m,I as p,c as l,a as e,b as d,f as b,w as g,t as i,F as v,r as _,e as x,o,d as k}from"./BVts8s_u.js";const w={class:"bg-white py-16 md:py-24"},z={class:"container mx-auto px-4 max-w-7xl"},P={class:"mb-8"},S={class:"flex items-center mb-4"},y={class:"bg-primary text-white text-xs font-bold px-3 py-1 rounded-full mr-3"},B={class:"text-gray-500"},A={class:"text-3xl md:text-4xl font-bold text-gray-900 mb-4"},D={class:"flex items-center"},I=["src","alt"],W={class:"font-medium text-gray-900"},E={class:"text-sm text-gray-500"},M={class:"relative rounded-lg overflow-hidden mb-10"},V=["src","alt"],F={class:"mt-16"},L={class:"grid grid-cols-1 md:grid-cols-2 gap-8"},N={class:"relative pb-2/3"},H=["src","alt"],q={class:"p-6"},C={class:"text-lg font-bold text-gray-900 mb-2"},G={class:"text-gray-700 mb-4"},K={__name:"[slug]",setup(T){p().params.slug;const c=[{id:"pflegegrad",name:"Pflegegrad"},{id:"alltagshilfe",name:"Alltagshilfe"},{id:"finanzierung",name:"Finanzierung"},{id:"betreuung",name:"Betreuung"}],n={title:"Pflegegrad beantragen: So geht's",date:"2023-11-15",image:"https://images.unsplash.com/photo-1576091160550-2173dba999ef?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80",categoryId:"pflegegrad",author:{name:"Julia Müller",role:"Pflegeexpertin",avatar:"https://images.unsplash.com/photo-1573497019940-1c28c88b4f3e?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"}},h=[{id:5,title:"Pflegegrad 2: Welche Leistungen stehen Ihnen zu?",slug:"pflegegrad-2-leistungen",excerpt:"Ein detaillierter Überblick über alle Leistungen, die bei Pflegegrad 2 in Anspruch genommen werden können.",image:"https://images.unsplash.com/photo-1454165804606-c3d57bc86b40?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"},{id:2,title:"Finanzielle Unterstützung für pflegende Angehörige",slug:"finanzielle-unterstuetzung-pflegende-angehoerige",excerpt:"Welche finanziellen Hilfen stehen pflegenden Angehörigen zu und wie können sie beantragt werden?",image:"https://images.unsplash.com/photo-1450101499163-c8848c66ca85?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"}],u=r=>{const t=c.find(s=>s.id===r);return t?t.name:""},f=r=>{const t={year:"numeric",month:"long",day:"numeric"};return new Date(r).toLocaleDateString("de-DE",t)};return(r,t)=>{const s=x;return o(),l("div",null,[e("section",w,[e("div",z,[d(s,{to:"/ratgeber",class:"inline-flex items-center text-primary font-medium mb-8 hover:text-primary-dark transition-colors"},{default:g(()=>t[0]||(t[0]=[e("svg",{xmlns:"http://www.w3.org/2000/svg",class:"h-5 w-5 mr-2",fill:"none",viewBox:"0 0 24 24",stroke:"currentColor"},[e("path",{"stroke-linecap":"round","stroke-linejoin":"round","stroke-width":"2",d:"M10 19l-7-7m0 0l7-7m-7 7h18"})],-1),k(" Zurück zur Übersicht ")])),_:1}),e("div",P,[e("div",S,[e("span",y,i(u(n.categoryId)),1),e("span",B,i(f(n.date)),1)]),e("h1",A,i(n.title),1),e("div",D,[e("img",{src:n.author.avatar,alt:n.author.name,class:"w-10 h-10 rounded-full mr-3"},null,8,I),e("div",null,[e("div",W,i(n.author.name),1),e("div",E,i(n.author.role),1)])])]),e("div",M,[e("img",{src:n.image,alt:n.title,class:"w-full h-auto"},null,8,V)]),t[3]||(t[3]=b('<div class="prose prose-lg max-w-none" data-v-6f62c2a9><p data-v-6f62c2a9> Wenn Sie oder ein Angehöriger pflegebedürftig werden, ist es wichtig, frühzeitig einen Pflegegrad zu beantragen. Der Pflegegrad bestimmt, welche Leistungen von der Pflegeversicherung übernommen werden. In diesem Artikel erklären wir Ihnen Schritt für Schritt, wie Sie einen Pflegegrad beantragen können. </p><h2 data-v-6f62c2a9>Was ist ein Pflegegrad?</h2><p data-v-6f62c2a9> Der Pflegegrad gibt an, wie selbstständig eine Person ihren Alltag bewältigen kann. Es gibt fünf Pflegegrade, wobei Pflegegrad 1 eine geringe Beeinträchtigung und Pflegegrad 5 eine schwerste Beeinträchtigung der Selbstständigkeit bedeutet. </p><h2 data-v-6f62c2a9>Wer kann einen Pflegegrad beantragen?</h2><p data-v-6f62c2a9> Jeder, der gesetzlich oder privat pflegeversichert ist und aufgrund körperlicher, kognitiver oder psychischer Beeinträchtigungen Hilfe im Alltag benötigt, kann einen Pflegegrad beantragen. Die Beeinträchtigungen müssen voraussichtlich für mindestens sechs Monate bestehen. </p><h2 data-v-6f62c2a9>Schritt 1: Antrag stellen</h2><p data-v-6f62c2a9> Der erste Schritt ist die Antragstellung bei der Pflegekasse. Dies kann telefonisch, schriftlich oder online erfolgen. Die Pflegekasse ist bei der Krankenkasse angesiedelt, bei der die pflegebedürftige Person versichert ist. </p><h2 data-v-6f62c2a9>Schritt 2: Vorbereitung auf die Begutachtung</h2><p data-v-6f62c2a9> Nach der Antragstellung wird ein Termin für die Begutachtung durch den Medizinischen Dienst (MD) oder bei privat Versicherten durch MEDICPROOF vereinbart. Zur Vorbereitung sollten Sie: </p><ul data-v-6f62c2a9><li data-v-6f62c2a9>Ein Pflegetagebuch führen, in dem Sie dokumentieren, welche Hilfe die pflegebedürftige Person benötigt</li><li data-v-6f62c2a9>Arztberichte und Befunde zusammenstellen</li><li data-v-6f62c2a9>Medikamentenpläne bereithalten</li><li data-v-6f62c2a9>Eine Liste mit den behandelnden Ärzten erstellen</li></ul><h2 data-v-6f62c2a9>Schritt 3: Die Begutachtung</h2><p data-v-6f62c2a9> Bei der Begutachtung kommt ein Gutachter zu Ihnen nach Hause und prüft die Selbstständigkeit in sechs verschiedenen Bereichen: </p><ol data-v-6f62c2a9><li data-v-6f62c2a9>Mobilität</li><li data-v-6f62c2a9>Kognitive und kommunikative Fähigkeiten</li><li data-v-6f62c2a9>Verhaltensweisen und psychische Problemlagen</li><li data-v-6f62c2a9>Selbstversorgung</li><li data-v-6f62c2a9>Umgang mit krankheits- oder therapiebedingten Anforderungen</li><li data-v-6f62c2a9>Gestaltung des Alltagslebens und sozialer Kontakte</li></ol><h2 data-v-6f62c2a9>Schritt 4: Entscheidung der Pflegekasse</h2><p data-v-6f62c2a9> Nach der Begutachtung erstellt der Gutachter einen Bericht, auf dessen Grundlage die Pflegekasse über den Pflegegrad entscheidet. Die Entscheidung sollte innerhalb von 25 Arbeitstagen nach Antragstellung erfolgen. </p><h2 data-v-6f62c2a9>Was tun bei Ablehnung oder zu niedrigem Pflegegrad?</h2><p data-v-6f62c2a9> Wenn der Antrag abgelehnt wird oder der zuerkannte Pflegegrad zu niedrig erscheint, können Sie innerhalb eines Monats Widerspruch einlegen. Legen Sie dem Widerspruch möglichst weitere ärztliche Atteste bei, die Ihre Pflegebedürftigkeit belegen. </p><h2 data-v-6f62c2a9>Fazit</h2><p data-v-6f62c2a9> Die Beantragung eines Pflegegrads mag zunächst kompliziert erscheinen, ist aber mit der richtigen Vorbereitung gut zu bewältigen. Scheuen Sie sich nicht, Hilfe in Anspruch zu nehmen, zum Beispiel durch Pflegestützpunkte oder Pflegeberatungsstellen. </p><p data-v-6f62c2a9> Bei SorglosPflege unterstützen wir Sie gerne bei allen Fragen rund um das Thema Pflege und helfen Ihnen, den passenden Pflegedienst zu finden. </p></div>',1)),e("div",F,[t[2]||(t[2]=e("h3",{class:"text-2xl font-bold text-gray-900 mb-6"},"Das könnte Sie auch interessieren",-1)),e("div",L,[(o(),l(v,null,_(h,a=>e("div",{key:a.id,class:"bg-white rounded-lg overflow-hidden shadow-md"},[d(s,{to:`/ratgeber/${a.slug}`,class:"block"},{default:g(()=>[e("div",N,[e("img",{src:a.image,alt:a.title,class:"absolute h-full w-full object-cover"},null,8,H)]),e("div",q,[e("h4",C,i(a.title),1),e("p",G,i(a.excerpt),1),t[1]||(t[1]=e("div",{class:"text-primary font-medium"},"Weiterlesen",-1))])]),_:2},1032,["to"])])),64))])])])])])}}},O=m(K,[["__scopeId","data-v-6f62c2a9"]]);export{O as default};
